function getTextInformation($texto){
    
    $palavras = explode(" ", $texto);
    $cont_prep = 0;
    $cont_verb = 0;
    $cont_primeira_pessoa = 0;

    foreach($palavras as $palavra){
    
        if(strlen($palavra) == 4 && strpos($palavra, 'l') === false) {
            if((substr($palavra, -1) == "s") || (substr($palavra, -1) == "j") || (substr($palavra, -1) == "n") || (substr($palavra, -1) == "c") || (substr($palavra, -1) == "q")){
                $cont_prep++;
            }
        }

        if(strlen($palavra) >= 7 ){
            if((substr($palavra, -1) != "s") && (substr($palavra, -1) != "j") && (substr($palavra, -1) != "n") && (substr($palavra, -1) != "c") && (substr($palavra, -1) != "q")){
                $cont_verb++;
            
                if(($palavra[0] == "s") || ($palavra[0] == "j") || ($palavra[0] == "n") || ($palavra[0] == "c") || ($palavra[0] == "q")){
                    $cont_primeira_pessoa++;
                }
            }
        }
    }
    
    return [
            "preposicoes" => $cont_prep,
            "verbos" => $cont_verb,
            "primeiraPessoa" => $cont_primeira_pessoa,
            ];

}

